        }
     }

     return (
        <div>
            <div className={styles.login__card}>
               <img src={login} alt=".."/>
               <h2>Register</h2>
                <div className={styles.input__inline__wrapper}>
                  <Input placeholder="First Name" value={firstName} onChange={(e) => setFirstName(e.target.value)}/>
                  <Input placeholder="Last Name" style={{marginLeft:'10px'}} value={lastName} onChange={(e) => setLastName(e.target.value)}/>
                  </div>
                  <div className={styles.input__wrapper}>
                    <Input placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)}/>
                  </div>
                  <div className={styles.input__wrapper}>
                    <Input.Password placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)}/>
                  </div>
                  <div className={styles.input__info}>
                     Existing User? <Link to="/Login">Register</Link>
                  </div> 
                 <Button type="primary" size="large" disabled={!username || !password} onClick={handleSubmit}>Register</Button>

            </div>
        </div>
    )
}

export default Register
